var gr = new GlideRecord('x_220966_invoice_approver_details');
gr.query();
var managers=[];
var order = 1;
var currentExecution=1;

while(gr.next()){
	var manager = {};
	manager.notified= false;
	var manager_sys_id=gr.getElement('invoice_approver');
	var gr_users = new GlideRecord('sys_user');
	gr_users.addQuery('sys_id', manager_sys_id);
	gr_users.query();
	if(gr_users.next()){
		//gs.info(JSON.stringify(gr_users));
		manager.email=gr_users.getValue('email');
		manager.name=gr_users.getValue('name');
		manager.approver_execution=gr.getValue('approver_execution');
		manager.approver_order=gr.getValue('approver_order');
		if(gr.getValue('approver_execution')=='Mandatory'){
			manager.order=order;
			order++;
		}else{
			manager.order=order;
		}
		managers.push(manager);
	}
	
}
workflow.scratchpad.managers=managers;
workflow.scratchpad.currentExecution=currentExecution;

//gs.info("Managers::"+JSON.stringify(managers));

