//gs.info("Managers::"+JSON.stringify(managers));

var manager ={};
//gs.info('currentExecution:'+currentExecution);
for(var i=1; i<=workflow.scratchpad.managers.length; i++){
	if(workflow.scratchpad.currentExecution==i){
		manager=workflow.scratchpad.managers[i-1];
		gs.info('manager.approver_execution:'+manager.approver_execution);
		if(manager.approver_execution =='Mandatory'){
			workflow.scratchpad.currentExecution++;
			break;
		}
	}
}
//gs.info('currentExecution:'+currentExecution);
gs.info("manager Details::"+JSON.stringify(manager));

