function getSelectedItems(){
	var gr = new GlideRecord('x_220966_invoice_selected_items');
	gr.addQuery('items_invoice_number','INVOICE1');
	gr.query();
	var totalAmount=0;
	var count=1;
	var itemList=[];
	var tableData="<table style='border:solid;'><tr><th style='padding: 20px;'>SL No</th><th style='padding: 20px;'>Item Name</th><th style='padding: 20px;'>Item Cost</th><th style='padding: 20px;'>Quantity</th><th style='padding: 20px;'>Total</th></tr>";
	while(gr.next()){
		var items={};
		tableData+="<tr>";
		//items.selected_item_name=gr.getValue('selected_item_name');
		//items.selected_item_cost=gr.getValue('selected_item_cost');
		//items.selected_items_quantity=gr.getValue('selected_items_quantity');
		//items.selected_items_quantity=gr.getValue('selected_items_quantity');
		totalAmount += gr.getValue('selected_item_cost')*gr.getValue('selected_items_quantity');
		itemList.push(items);
		
		tableData+="<td style='padding: 10px 20px;'>"+(count++)+"</td>";
		tableData+="<td style='padding: 10px 20px;'>"+gr.getValue('selected_item_name')+"</td>";
		tableData+="<td style='padding: 10px 20px;'>"+gr.getValue('selected_item_cost')+"</td>";
		tableData+="<td style='padding: 10px 20px;'>"+gr.getValue('selected_items_quantity')+"</td>";
		tableData+="<td style='padding: 10px 20px;'>"+(gr.getValue('selected_item_cost')*gr.getValue('selected_items_quantity'))+"</td>";
		
		tableData+="</tr>";
	}
	tableData+="<tr><td colspan=3 ><center>TOTAL</center><td><td style='border-top:solid;padding:0px;'><B>"+totalAmount+"</B><td></tr></table>";
	gs.info('totalAmount:'+totalAmount);
	current.u_invoice_amount=totalAmount;
	gs.info('tableData:'+tableData);
	workflow.scratchpad.itemsList=itemList;
	workflow.scratchpad.tableData=tableData;
}
var selectedItems = getSelectedItems();

