function getAllManagers(){
	var gr = new GlideRecord('x_220966_invoice_approver_details');
	gr.orderBy('approver_order');
	gr.query();
	var managers=[];
	while(gr.next()){
		var manager = {};
		manager.notified= false;
		var manager_sys_id=gr.getElement('invoice_approver');
		var gr_users = new GlideRecord('sys_user');
		gr_users.addQuery('sys_id', manager_sys_id);
		gr_users.query();
		if(gr_users.next()){
			//gs.info(JSON.stringify(gr_users));
			manager.email=gr_users.getValue('email');
			manager.name=gr_users.getValue('name');
			manager.userId=gr_users.getValue('user_name');
			manager.approver_execution=gr.getValue('approver_execution');
			manager.approver_order=gr.getValue('approver_order');
			managers.push(manager);
		}
	}
	//gs.info('All Managers List::'+JSON.stringify(managers));
	return managers;
}

var managerList= getAllManagers();
var managers=[];
var multiVal=[];
for(var i=0;i<managerList.length;i++){
	if(managerList[i]['approver_execution']=='Mandatory'){
		if(multiVal.length>0){
			managers.push(multiVal);
			multiVal=[];
		}
		var level=[];
		level.push(managerList[i]);
		managers.push(level);
		
	}else{
		multiVal.push(managerList[i]);
		if(i==managerList.length-1){
			managers.push(multiVal);
		}
	}
}

